from django.conf.urls import urlfrom django.contrib.staticfiles.urls import staticfiles_urlpatternsfrom django.views.generic import RedirectViewfrom . import viewsfrom . import list_json, data_apiurlpatterns = [    url(r'^index/$', views.index, name='index'),    url(r'^liangsongyitang_json/$', list_json.liangsongyitang_json, name='liangsongyitang_json'),    url(r'^form_handle/$', views.form_handle, name='form_handle'),    url(r'^s_action/$', list_json.s_action, name='s_action'),    url(r'^pri_edu_api/$', data_api.Pri_Edu_Api.as_view()),    url(r'^mid_edu_api/$', data_api.Mid_Edu_Api.as_view()),]urlpatterns += staticfiles_urlpatterns()urlpatterns += [    url(r'^$', RedirectView.as_view(url='/index/', permanent=True)),]